<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'includes/auth.php';require_once 'class/make.php';$makeObject = new class_make(); if(isset($_GET['action']) && trim($_GET['action']) == 'tablesearch') {	$search = isset($_REQUEST['search']) && trim($_REQUEST['search']) != '' ? trim($_REQUEST['search']) : null;	$start 	= isset($_REQUEST['iDisplayStart']) ? $_REQUEST['iDisplayStart'] : 0;	$length = isset($_REQUEST['iDisplayLength']) ? $_REQUEST['iDisplayLength'] : 20;		$makeData = $makeObject->getSearch($search, $start, $length);	$all = array();	if($makeData) {		for($i = 0; $i < count($makeData['records']); $i++) {			$item = $makeData['records'][$i];			$all[$i] = array(								$item['brand_name'], 								'<a href="/brand/make/details.php?code='.$item['make_code'].'">'.$item['make_name'].'</a>', 								$item['model_count']);		}	}		if($makeData) {		$response['sEcho'] = $_REQUEST['sEcho'];		$response['iTotalRecords'] = $makeData['displayrecords'];				$response['iTotalDisplayRecords'] = $makeData['count'];		$response['aaData']	= $all;	} else {		$response['result'] 	= false;		$response['message']	= 'There are no items to show.';				}	    echo json_encode($response);		$makeObject = $search = $start = $length = $makeData = $i = $item = $all = $response = null;	unset($makeObject, $search, $start, $length, $makeData, $i, $item, $all, $response);	    die();	}$smarty->display('brand/make/default.tpl');$makeObject = null;unset($makeObject);?>