<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'includes/auth.php';/* objects. */require_once 'class/make.php';require_once 'class/brand.php';require_once 'class/File.php';$makeObject 	= new class_make();$brandObject	= new class_brand();$fileObject 	= new File(array('gif', 'png', 'jpg', 'jpeg', 'gif'));if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$makeData = $makeObject->getByCode($code);		if($makeData) {		$smarty->assign('makeData', $makeData);	} else {		header('Location: /brand/make/');		exit;			}}$brandpairs = $brandObject->pairs();if($brandpairs) $smarty->assign('brandpairs', $brandpairs);/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;	$areaByName	= NULL;		if(isset($_POST['make_name']) && trim($_POST['make_name']) == '') {		$errorArray['make_name'] = 'Name of make is required';		$formValid = false;			}		if(isset($_POST['brand_code']) && trim($_POST['brand_code']) == '') {		$errorArray['brand_code'] = 'Brand of the make is required';		$formValid = false;			}		if(isset($_FILES['makelogo'])) {		/* Check validity of the CV. */		if((int)$_FILES['makelogo']['size'] != 0 && trim($_FILES['makelogo']['name']) != '') {			/* Check if its the right file. */			$ext = $fileObject->file_extention($_FILES['makelogo']['name']); 			if($ext != '') {								$checkExt = $fileObject->getValidateExtention('makelogo', $ext);								if(!$checkExt) {					$errorArray['makelogo'] = 'Invalid file type something funny with the file format';					$formValid = false;										} else {					/* Check width and height */					$makelogo = getimagesize($_FILES['makelogo']['tmp_name']);										if($makelogo[0] < 150 || $makelogo < 150) {						$errorArray['makelogo'] = 'Image needs to have a width more than 150 pixels as well as a height that is more than 150 pixels';						$formValid = false;												}				}			} else {				$errorArray['makelogo'] = 'Invalid file type';				$formValid = false;												}		} else {						switch((int)$_FILES['makelogo']['error']) {				case 1 : $errorArray['makelogo'] = 'The uploaded file exceeds the maximum upload file size, should be less than 1M'; $formValid = false; break;				case 2 : $errorArray['makelogo'] = 'File size exceeds the maximum file size'; $formValid = false; break;				case 3 : $errorArray['makelogo'] = 'File was only partically uploaded, please try again'; $formValid = false; break;				//case 4 : $errorArray['makelogo'] = 'No file was uploaded'; $formValid = false; break;				case 6 : $errorArray['makelogo'] = 'Missing a temporary folder'; $formValid = false; break;				case 7 : $errorArray['makelogo'] = 'Faild to write file to disk'; $formValid = false; break;			}		}	}			if(count($errorArray) == 0 && $formValid == true) {				$data 	= array();			$data['make_name']	= trim($_POST['make_name']);						$data['brand_code']	= trim($_POST['brand_code']);				if(isset($makeData)) {			/*Update. */			$where		= $makeObject->getAdapter()->quoteInto('make_code = ?', $makeData['make_code']);			$success	= $makeObject->update($data, $where);				$success	= $makeData['make_code'];		} else {			$success = $makeObject->insert($data);		}				/* Upload image if its added. */		if((int)$_FILES['makelogo']['size'] != 0 && trim($_FILES['makelogo']['name']) != '') {						$image = array();			$image['make_image_name']	= $fileObject->getRandomFileName();			$image['make_image_path']	= '';			$image['make_image_ext']	= '';						$ext 		= strtolower($fileObject->file_extention($_FILES['makelogo']['name']));								$filename	= $image['make_image_name'].'.'.$ext;						$directory	= realpath(__DIR__.'/../../../public_html/').'/media/make/'.$success.'/logo/';			$file		= $directory.$filename;				if(!is_dir($directory)) mkdir($directory, 0777, true);			/* Create files for this product type. */			foreach($fileObject->logo as $item) {								$newfilename = str_replace($filename, $item['code'].$filename, $file);								/* Create new file and rename it. */				$uploadObject	= PhpThumbFactory::create($_FILES['makelogo']['tmp_name']);				$uploadObject->resize($item['width'], $item['height']);				$uploadObject->save($newfilename);						}			$image['make_image_path']	= '/media/make/'.$success.'/logo/';			$image['make_image_ext']	= '.'.$ext;						$where = $makeObject->getAdapter()->quoteInto('make_code = ?', $success);			$makeObject->update($image, $where);							}				if(count($errorArray) == 0) {			header('Location: /brand/make/');				exit;				}					}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('brand/make/details.tpl');?>