<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'includes/auth.php';require_once 'class/model.php';$modelObject = new class_model(); if(isset($_GET['action']) && trim($_GET['action']) == 'tablesearch') {	$search = isset($_REQUEST['search']) && trim($_REQUEST['search']) != '' ? trim($_REQUEST['search']) : null;	$start 	= isset($_REQUEST['iDisplayStart']) ? $_REQUEST['iDisplayStart'] : 0;	$length = isset($_REQUEST['iDisplayLength']) ? $_REQUEST['iDisplayLength'] : 20;		$modelData = $modelObject->getSearch($search, $start, $length);	$all = array();	if($modelData) {		for($i = 0; $i < count($modelData['records']); $i++) {			$item = $modelData['records'][$i];			$all[$i] = array( 								$item['brand_name'], 								$item['make_name'], 								'<a href="/brand/model/details.php?code='.$item['model_code'].'">'.$item['model_name'].'</a>', 								$item['car_count']);		}	}		if($modelData) {		$response['sEcho'] = $_REQUEST['sEcho'];		$response['iTotalRecords'] = $modelData['displayrecords'];				$response['iTotalDisplayRecords'] = $modelData['count'];		$response['aaData']	= $all;	} else {		$response['result'] 	= false;		$response['message']	= 'There are no items to show.';				}	    echo json_encode($response);    die();	}$smarty->display('brand/model/default.tpl');?>