<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'includes/auth.php';/* objects. */require_once 'class/brand.php';require_once 'class/File.php';$brandObject 	= new class_brand();$fileObject 	= new File(array('gif', 'png', 'jpg', 'jpeg', 'gif'));if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$brandData = $brandObject->getByCode($code);		if($brandData) {		$smarty->assign('brandData', $brandData);	} else {		header('Location: /brand/view/');		exit;			}}/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;	$areaByName	= NULL;		if(isset($_POST['brand_name']) && trim($_POST['brand_name']) == '') {		$errorArray['brand_name'] = 'required';		$formValid = false;			}		if(isset($_FILES['brandlogo'])) {		/* Check validity of the CV. */		if((int)$_FILES['brandlogo']['size'] != 0 && trim($_FILES['brandlogo']['name']) != '') {			/* Check if its the right file. */			$ext = $fileObject->file_extention($_FILES['brandlogo']['name']); 			if($ext != '') {								$checkExt = $fileObject->getValidateExtention('brandlogo', $ext);								if(!$checkExt) {					$errorArray['brandlogo'] = 'Invalid file type something funny with the file format';					$formValid = false;										} else {					/* Check width and height */					$brandlogo = getimagesize($_FILES['brandlogo']['tmp_name']);										if($brandlogo[0] < 150 || $brandlogo < 150) {						$errorArray['brandlogo'] = 'Image needs to have a width more than 150 pixels as well as a height that is more than 150 pixels';						$formValid = false;												}				}			} else {				$errorArray['brandlogo'] = 'Invalid file type';				$formValid = false;												}		} else {						switch((int)$_FILES['brandlogo']['error']) {				case 1 : $errorArray['brandlogo'] = 'The uploaded file exceeds the maximum upload file size, should be less than 1M'; $formValid = false; break;				case 2 : $errorArray['brandlogo'] = 'File size exceeds the maximum file size'; $formValid = false; break;				case 3 : $errorArray['brandlogo'] = 'File was only partically uploaded, please try again'; $formValid = false; break;				//case 4 : $errorArray['brandlogo'] = 'No file was uploaded'; $formValid = false; break;				case 6 : $errorArray['brandlogo'] = 'Missing a temporary folder'; $formValid = false; break;				case 7 : $errorArray['brandlogo'] = 'Faild to write file to disk'; $formValid = false; break;			}		}	}		if(count($errorArray) == 0 && $formValid == true) {				$data 	= array();			$data['brand_name']				= trim($_POST['brand_name']);							if(isset($brandData)) {			/*Update. */			$where		= $brandObject->getAdapter()->quoteInto('brand_code = ?', $brandData['brand_code']);			$success	= $brandObject->update($data, $where);				$success	= $brandData['brand_code'];		} else {			$success = $brandObject->insert($data);		}				/* Upload image if its added. */		if((int)$_FILES['brandlogo']['size'] != 0 && trim($_FILES['brandlogo']['name']) != '') {						$image = array();			$image['brand_image_name']	= $fileObject->getRandomFileName();			$image['brand_image_path']	= '';			$image['brand_image_ext']	= '';						$ext 		= strtolower($fileObject->file_extention($_FILES['brandlogo']['name']));								$filename	= $image['brand_image_name'].'.'.$ext;						$directory	= realpath(__DIR__.'/../../../public_html/').'/media/brand/'.$success.'/logo/';			$file		= $directory.$filename;				if(!is_dir($directory)) mkdir($directory, 0777, true);			/* Create files for this product type. */			foreach($fileObject->logo as $item) {								$newfilename = str_replace($filename, $item['code'].$filename, $file);								/* Create new file and rename it. */				$uploadObject	= PhpThumbFactory::create($_FILES['brandlogo']['tmp_name']);				$uploadObject->resize($item['width'], $item['height']);				$uploadObject->save($newfilename);						}			$image['brand_image_path']	= '/media/brand/'.$success.'/logo/';			$image['brand_image_ext']	= '.'.$ext;						$where = $brandObject->getAdapter()->quoteInto('brand_code = ?', $success);			$brandObject->update($image, $where);							}				if(count($errorArray) == 0) {			header('Location: /brand/view/');				exit;				}					}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('brand/view/details.tpl');?>